INSERT INTO Recipes (KitchenId, RecipeCategoryId, Title, Description, Portions)
VALUES
({{kitchenId}}, 1, N'Классический борщ', N'Говяжий бульон, свекла, капуста, картофель, морковь, лук, томатная паста. Медленное тушение свеклы для насыщенного цвета. Подаётся со сметаной и зеленью.', 6),
({{kitchenId}}, 8, N'Крем-суп из шампиньонов', N'Шампиньоны, лук, сливки, чеснок, бульон. Готовится до мягкости и пюрируется блендером. Подаётся с гренками.', 4),
({{kitchenId}}, 9, N'Прозрачный куриный бульон', N'Куриные кости, лук, морковь, петрушка. Варить долго на медленном огне для прозрачности.', 8),
({{kitchenId}}, 10, N'Окрошка классическая', N'Кефир/сыворотка, варёная колбаса/мясо, огурцы, яйца, зелень, картофель. Освежающий холодный суп на лето.', 6),

({{kitchenId}}, 11, N'Тушёная говядина в томате', N'Говядина, томатная паста, лук, морковь, специи. Тушить до мягкости, подавать с пюре.', 4),
({{kitchenId}}, 12, N'Запечённая семга с лимоном', N'Филе семги, лимон, оливковое масло, укроп. Запекать до готовности, подавать с овощами на гриле.', 2),
({{kitchenId}}, 13, N'Паста карбонара', N'Спагетти, бекон, яйца, пармезан, перец. Быстро готовится — кремовая текстура без сливок.', 3),
({{kitchenId}}, 14, N'Курица на гриле с маринадом', N'Курица, соус на основе лимона и чеснока, мариновать минимум 2 часа, жарить на гриле до золотистой корочки.', 4),

({{kitchenId}}, 15, N'Овощной салат с авокадо', N'Авокадо, помидоры, огурцы, красный лук, оливковое масло, лимонный сок. Лёгкий, свежий.', 2),
({{kitchenId}}, 16, N'Салат с курицей и ананасом', N'Копчёная/варёная курица, ананасы, майонез/йогурт, сыр, кукуруза. Сытный салат для пикника.', 4),
({{kitchenId}}, 17, N'Тёплый салат с говядиной и грибами', N'Говядина, шампиньоны, руккола, бальзамический соус. Подаётся тёплым.', 3),

({{kitchenId}}, 18, N'Брускетта с томатами', N'Хрустящий хлеб, томаты, базилик, чеснок, оливковое масло. Быстрая закуска.', 4),
({{kitchenId}}, 19, N'Жюльен с грибами и сыром', N'Шампиньоны, сливки, сыр, запечённый в кокотницах.', 6),

({{kitchenId}}, 20, N'Шоколадный торт "Нежность"', N'Бисквит, шоколадный крем, прослойки с малиновым джемом. Рецепт для праздника.', 10),
({{kitchenId}}, 21, N'Эклеры с заварным кремом', N'Заварное тесто, заварной крем на молоке, глазурь.', 12),
({{kitchenId}}, 22, N'Домашнее ванильное мороженое', N'Сливки, молоко, ваниль, сахар. Медленное замораживание для кремовой текстуры.', 6),

({{kitchenId}}, 23, N'Лимонад с мятой', N'Свежевыжатый лимонный сок, сахарный сироп, мята, газированная вода. Освежающий напиток.', 4),
({{kitchenId}}, 24, N'Глинтвейн с апельсином', N'Красное вино, апельсин, специи (корица, гвоздика), сахар. Подогреть, но не кипятить.', 6),

({{kitchenId}}, 25, N'Тартар из помидоров с базиликом (холодный соус)', N'Помидоры, базилик, оливковое масло, бальзамический крем. Подаётся к хлебу и мясу.', 4),
({{kitchenId}}, 26, N'Горячий соус терияки', N'Соевый соус, мёд, имбирь, чеснок. Уварить до нужной вязкости, отлично подходит для глазирования мяса.', 8),

